import React, {useState} from 'react'

const App = () => {
    const [clicks, setClicks] = useState({
        left: 0, right: 0
    })

    return (
        <div>
            {clicks.left}
            <button onClick={() => setClicks(clicks.left + 2)}>
                left
            </button>
            <button onClick={() => setClicks(clicks.right + 2)}>
                right
            </button>
            {clicks.right}
        </div>
    )
}

export default App
